import styles from '../css/CartSlider.module.css'

const CartSlider = ({ isCartOpen, closeCart, cartItems }) => {
    return (
        isCartOpen && (
            <div className={styles.cartSliderOverlay} onClick={closeCart}>
                <div 
                  className={`${styles.cartSlider} ${isCartOpen ? styles.open : ''}`}
                  onClick={(e) => e.stopPropagation()}
                >
                    <h2>Your Cart</h2>
                    {cartItems.map(item => (
                        <div key={item.id} className={styles.cartItem}>
                            <img src={item.image} alt={item.name} className={styles.itemImage} />
                            <div className={styles.itemInfo}>
                                <p className={styles.itemName}>{item.name}</p>
                                <p className={styles.itemPrice}>${item.price.toFixed(2)}</p>
                            </div>
                            <div className={styles.itemActions}>
                                <button className={styles.itemQuantityButton}>-</button>
                                <span className={styles.itemQuantity}>1</span>
                                <button className={styles.itemQuantityButton}>+</button>
                            </div>
                        </div>
                    ))}
                    <button className={styles.checkoutButton}>Checkout</button>
                </div>
            </div>
        )
    )
}

export default CartSlider;
