import { useState } from 'react'
import styles from '../css/AddProduct.module.css'

const AddProduct = ({ product, quantity, onClose }) => {

    const [localQuantity, setQuantity] = useState(quantity);

    const increment = () => setQuantity(prev => prev + 1);
    const decrement = () => setQuantity(prev => prev - 1);

    const handleOutsideClick = (event) => {
        if(event.currentTarget === event.target){
            onClose();
        }
    }

    return (
        <div className={styles.backdrop} onClick={handleOutsideClick}>
            <div className={styles.modal}>
                <div className={styles.productInfo}>
                    <img src={product.image} alt={product.name} className={styles.productImage} />
                    <div className={styles.productDetails}>
                        <h3>{product.name}</h3>
                        <p>${product.price.toFixed(2)}</p>
                    </div>
                </div>
                <div className={styles.quantityControls}>
                    <button className={styles.qbutton} onClick={decrement}>-</button>
                    <div>{localQuantity}</div>
                    <button className={styles.qbutton} onClick={increment}>+</button>
                </div>
            </div>
        </div>
    )
}

export default AddProduct;
