import { useState } from 'react';
import styles from '../css/ProductPage.module.css';
import NavBar from '../components/NavBar';
import SubMenu from '../components/SubMenu';
import ProductCatalog from '../components/ProductCatalog';
import CartSlider from '../components/CartSlider';
import allProducts from '../data/allProducts';

const ProductPage = () => {
  const [isCartOpen, setCartOpen] = useState(false);
  const cartItems = [...allProducts]; // replace with actual cart items

  const handleCartOpen = () => {
    setCartOpen(true);
  };

  const handleCartClose = () => {
    setCartOpen(false);
  };

  return (
    <div className={styles.container}>
      <NavBar onCartClick={handleCartOpen} />
      <div className={styles.pageBody}>
        <SubMenu />
        <ProductCatalog list={allProducts} />
      </div>
      <CartSlider isCartOpen={isCartOpen} closeCart={handleCartClose} cartItems={cartItems} />
    </div>
  );
};

export default ProductPage;
