import { useEffect } from 'react';
import styles from '../css/CartSlider.module.css';

const CartSlider = ({ isCartOpen, closeCart, cartItems }) => {
  // Add event listener to handle clicks outside of the cart
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (event.target.classList.contains(styles.overlay)) {
        closeCart();
      }
    };

    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, [closeCart, styles]);

  const sliderClasses = isCartOpen ? `${styles.cartContainer} ${styles.open}` : styles.cartContainer;

  return (
    <div className={styles.overlay}>
      <div className={sliderClasses}>
        {cartItems.map((item, index) => (
          <div key={index} className={styles.cartItem}>
            <h2>{item.name}</h2>
            <p>${item.price.toFixed(2)}</p>
            <input type="number" min="1" defaultValue="1" />
          </div>
        ))}
        <button onClick={closeCart} className={styles.checkoutButton}>Checkout</button>
      </div>
    </div>
  );
};

export default CartSlider;
