import { useState } from 'react';
import styles from '../css/ProductPage.module.css';
import NavBar from '../components/NavBar';
import SubMenu from '../components/SubMenu';
import ProductCatalog from '../components/ProductCatalog';
import CartSlider from '../components/CartSlider';
import allProducts from '../data/allProducts';

const ProductPage = () => {


    //cart
    const [isCartOpen, setCartOpen] = useState(false);
    const cartItems = [...allProducts];

    const handleCartClick= ()=> {

    setCartOpen(!isCartOpen)

  }
    //category
    const [selectedCategory, setCategory] = useState("All");

    const [productList, setProductList] = useState(allProducts);



    const handleCategoryChange = (category) => {

        if(category === "All"){
        setProductList(allProducts)
        setCategory("All")
        } else
        {const filteredProductList = allProducts.filter(
            (product) => product.category === category
        );
         setProductList(filteredProductList);
        setCategory(category)}
     };


     //add product

     const [isAddProductOpen, setAddProductOpen] = useState(false);
    const [currentProduct, setCurrentProduct] = useState(null);

    const handleProductClick = (product) => {
        setCurrentProduct(product);
        setAddProductOpen(true);
    }

    const closeAddProduct = () => {
        setAddProductOpen(false);
    }




  return (
    <div className={styles.container}>
      <NavBar onCartClick={handleCartClick}/>
      <div className={styles.pageBody}>
        <SubMenu handleCategoryChange={handleCategoryChange} category={selectedCategory}/>
        <ProductCatalog list={productList} onProductClick={handleProductClick}/>
      </div>
      {isCartOpen && <CartSlider cartItems={cartItems} onClose={handleCartClick} />}
      {isAddProductOpen && currentProduct && <AddProduct product={currentProduct} quantity={1} onClose={closeAddProduct} />}
    </div>
  );
};

export default ProductPage;
